class Question {
  int id;
  String title;
  String content;
  int genre;
  List<Option> options;
  String image;

  Question(
      {this.id,
      this.title,
      this.content,
      this.genre,
      this.options,
      this.image});

  static Question fromMap(Map<String, dynamic> map) {
    if (map == null) return null;
    Question questionBean = Question();
    questionBean.id = map['id'];
    questionBean.image = map['image'];
    questionBean.title = map['title'];
    questionBean.content = map['content'];
    questionBean.genre = map['genre'];
    questionBean.options = List()
      ..addAll((map['options'] as List ?? []).map((o) => Option.fromMap(o)));
    return questionBean;
  }
}

